int find_name (const char *file, const char *filename, lexgrog *p_lg)
{
	int ret;

	if (strcmp (file, "-") == 0) {
		yyin = fdopen (dup (0), "r");
	} else {
#ifdef COMP_SRC
		struct compression *comp;
#endif /* COMP_SRC */
		struct stat st;

		if (stat (file, &st)) {
			error (0, errno, "%s", file);
			return 0;
		}

		if (S_ISDIR (st.st_mode)) {
			error (0, EISDIR, "%s", file);
			return 0;
		}

#ifdef COMP_SRC
		/* See if we need to decompress the file(s) first. */
		comp = comp_info (file, 0);
		if (comp) {
			file = decompress (file, comp);
			if (!file)
				return 0;
		}
#endif /* COMP_SRC */
		drop_effective_privs ();
		yyin = fopen (file, "r");
		regain_effective_privs ();
		if (!yyin) {
			error (0, errno, _("can't open %s"), file);
#ifdef COMP_SRC
			remove_ztemp ();
#endif /* COMP_SRC */
			return 0;
		}
	}

	fname = filename;
	*(p_name = newname) = '\0';
	memset (filters, '_', sizeof (filters));

	fill_mode = 1;
	waiting_for_quote = 0;

	if (p_lg->type)
		BEGIN (CAT_FILE);
	else
		BEGIN (MAN_FILE);

	drop_effective_privs ();

	yyrestart (yyin);
	ret = yylex ();
	fclose (yyin);

#ifdef COMP_SRC
	remove_ztemp ();
#endif

	regain_effective_privs ();

	if (ret)
		return 0;
	else {
		char f_tmp[MAX_FILTERS];
		int j, k;

		/* wipe out any leading or trailing spaces */
		if (*newname) {
			for (p_name = strchr (newname, '\0');
			     *(p_name - 1) == ' ';
			     p_name--);
			if (*p_name == ' ')
				*p_name = '\0';
		}
		for (p_name = newname; *p_name == ' '; p_name++);
		p_lg->whatis = xstrdup (p_name);
		memset (f_tmp, '\0', MAX_FILTERS);
		f_tmp[0] = '-';
		for (j = k = 0; j < MAX_FILTERS; j++)
			if (filters[j] != '_')
				f_tmp[k++] = filters[j];
		p_lg->filters = xstrdup (f_tmp);
		return p_name[0];
	}
}

